import { SUBMIT } from '../actions/actionsTypes';

const INICIAL_STATE = ({
  clientsData: [{ 
  name:"",
  age:"",
  email: "",
}]});

const clientsReducer = (state = INICIAL_STATE, action) => {
  switch(action.type) {
    case SUBMIT:
      return ([...state, {
        name: state.payload.name,
        age: state.payload.age,
        email: state.payload.email,
      }]);
    default:
      return state;
  }
}

export default clientsReducer;
